mapper.select:
select count(1) from gd_order where 1 = 1

    and orderid like CONCAT('%',#{orderId},'%')



    and orderTime >= #{startTime}


    and orderTime <= #{endTime}



    and orderStat = #{orderStat}


    and storeid = #{storeId}


    and vipId like CONCAT('%',#{vipPhone},'%')


    and orderscene = #{orderScene}

select * from gd_order where 1 = 1

    and orderid like CONCAT('%',#{orderId},'%')



    and userId like CONCAT('%',#{userId},'%')



    and orderTime >= #{startTime}


    and orderTime <= #{endTime}



    and orderStat = #{orderStat}


    and storeid = #{storeId}


    and vipId like CONCAT('%',#{vipPhone},'%')


    and orderscene = #{orderScene}

order by orderTime DESC limit #{pageNo},#{pageSize}

SELECT YEAR(o.orderTime) as '2019',
SUM(CASE WHEN MONTH(o.orderTime) = #{mones} THEN o.ordermoney ELSE 0 END) as  'summoney',
o.storeid as 'storeid'
FROM `Order`.gd_order o
where YEAR(o.orderTime) = YEAR(#{selyear})
and MONTH(o.orderTime) = #{mones}
and o.storeid = #{storeid}
GROUP BY YEAR(o.orderTime);
